mapper.select:
select b.*
from
(select a.*, rownum rn from  (select post.postid,post.userid, post.posttitle, post.postcontent, 
post.postred_dt, post.postid2,post.boardid,postuse_yn, lpad(' ', (level-1)*4) || post.posttitle, level lv from jsppost post where boardid=#{boardid}

start with postid2 is null connect by prior postid = postid2)a)b where 	rn >= (#{page}-1)*#{pageSize}+1 and rn<= #{page}*#{pageSize}
